import { useState } from "react";
import "./App.css";
import Axios from "axios";

export function getEmployees1() {
  Axios.get("http://localhost:3001/employees").then((response) => {
    setEmployeeList(response.data);
  });
  return employeeList;
}

function App() {
  // States
  // Employees
  const [name, setName] = useState("");
  const [employeeList, setEmployeeList] = useState([]);

  // Carmodels
  //const [id, setId] = useState(0);
  const [brand, setBrand] = useState("");
  const [model, setModel] = useState("");
  const [price, setPrice] = useState(0);

  // Gets information from DB
  const [carmodelList, setCarmodelList] = useState([]);

  const getEmployees = () => {
    Axios.get("http://localhost:3001/employees").then((response) => {
      setEmployeeList(response.data);
    });
  };

  const addCarmodel = () => {
    Axios.post("http://localhost:3001/carmodels", {
      brand: brand,
      model: model,
      price: price,
    }).then(() => {
      console.log("Success");
    });
  };

  const getCarmodels = () => {
    Axios.get("http://localhost:3001/carmodels").then((response) => {
      setCarmodelList(response.data);
    });
  };

  return (
    <div className="App">
      <div className="information">
        <label>Brand:</label>
        <input
          type="text"
          onChange={(event) => {
            setBrand(event.target.value);
          }}
        />
        <label>Model:</label>
        <input
          type="number"
          onChange={(event) => {
            setModel(event.target.value);
          }}
        />
        <label>Price:</label>
        <input
          type="text"
          onChange={(event) => {
            setPrice(event.target.value);
          }}
        />
        <button onClick={addCarmodel}>Add Carmodel</button>
      </div>

      <div className="employees">
        <button onClick={getCarmodels}>Show Carmodels</button>
        {carmodelList.map((val, key) => {
          return (
            <div className="employee">
              <h3>Brand: {val.brand}</h3>
              <h3>Model: {val.model}</h3>
              <h3>Price: {val.price}</h3>
            </div>
          );
        })}
      </div>

      <div className="employees">
        <button onClick={getEmployees}>Show Employees</button>
        {employeeList.map((val, key) => {
          return (
            <div className="employee">
              <h3>Name: {val.name}</h3>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
