import React from "react";
import { NavLink } from "react-router-dom";

function Home() {
  const user = () => {
    // if username matches user, then give that page the info of that user
    // if user == admin, send to admin page
    var userName = document.getElementById("user").value;
    if (userName === "admin") {
      history.push("/admin");
    }
  };

  return (
    <>
      <div className="home">
        <div class="container">
          <div class="row align-items-center my-5">
            <div class="col-lg-7">
              <img
                class="img-fluid rounded mb-4 mb-lg-0"
                src="http://placehold.it/900x400"
                alt=""
              />
            </div>
            <div class="col-lg-5">
              <h1 class="font-weight-light">Carshop</h1>
              <p>Welcome to Carshop.</p>
            </div>
            <div>
              <label>User name:</label>
              <input type="text" id="user"></input>
            </div>
            <div>
              <label>Password:</label>
              <input type="text"></input>
            </div>
            <div>
              <NavLink to="/admin">Log in admin</NavLink>
            </div>
            <div>
              <NavLink to="/employee">Log in employee</NavLink>
            </div>
            <button onClick={user}>Log in</button>
          </div>
        </div>
      </div>
    </>
  );
}

export default Home;
